import { useState } from 'react'
import './ExpenseForm.css'

const ExpenseForm = (props) => {
 	
	const [enteredTitle, setEnteredTitle] = useState('')
	const [enteredAmount, setEnteredAmount] = useState('')
	const [enteredDate, setEnteredDate] = useState('')

	const [cancelExpense, setCancelExpense] = useState(false)
	
	const titleChangeHandler = (event) => {
		setEnteredTitle(event.target.value);
	}
	
	const amountChangeHandler = (event) => {
		setEnteredAmount(event.target.value);
	}

	const dateChangeHandler = (event) => {
		setEnteredDate(event.target.value);
	}

	const submitHandler = (event) => {
		event.preventDefault()
		const expenseData = {
			title: enteredTitle,
			amount: enteredAmount,
			date: new Date(enteredDate)
		}
		
		props.onSaveExpenseData(expenseData)
		setEnteredTitle('');
		setEnteredAmount('');
		setEnteredDate('');
		props.resetNewExpense()
	}

	const CancelExpenseHandler = (event) => {
		setCancelExpense(true)
	}

	// if (cancelExpense === true) {
	// 	return <h1>canceled</h1>
	// }

	return (

		<form onSubmit={submitHandler}>
			<div className='new-expense__controls'>
				<div className='new-expense__control'>
					<label>Title</label>
					<input type='text' value={enteredTitle} onChange={titleChangeHandler} required/>
				</div>
				<div className='new-expense__control'>
					<label>Amount</label>
					<input type='number' min='0.01' step='0.01' value={enteredAmount} onChange={amountChangeHandler} required/>
				</div>
				<div className='new-expense__control'>
					<label>Date</label>
					<input type='date' min='2021-01-01' max='2024-12-31' value={enteredDate} onChange={dateChangeHandler} required/>
				</div>
			</div>
			<div className='new-expense__actions'>
				<button type='submit'>Add Expense</button>
			</div>
			<button value="false" onClick={CancelExpenseHandler}>Cancel</button>
		</form>)
}

export default ExpenseForm
